import React, { useEffect, useState } from "react";
import Event from "./event";
import { Login } from "./views/index";
import { GlobalStyle } from "../styles";
import { BrowserRouter, Routes, Route, useLocation } from "react-router-dom";

import { accessToken, logout, getCurrentUserProfile } from "./spotify";
import { catchErrors } from "./utils";

function ScrollToTop() {
	const { pathname } = useLocation();

	useEffect(() => {
		window.scrollTo(0, 0);
	}, [pathname]);

	return null;
}

function App() {
	const [token, setToken] = useState(null);
	const [profile, setProfile] = useState(null);

	useEffect(() => {
		setToken(accessToken);
		const fetchData = async () => {
			const { data } = await getCurrentUserProfile();
			setProfile(data);
			console.log(data);
		};

		catchErrors(fetchData());
	}, []);

	return (
		<div className="App">
			<GlobalStyle />
			{!token ? (
				<Login />
			) : (
				<>
					<ScrollToTop />
					<Routes>
						<Route
							path="/"
							element={
								<>
									{profile ? (
										<div>
											<button onClick={logout}>Log out</button>{" "}
											<div>{profile.display_name}</div>
											<img src={profile.images[0].url}></img>
										</div>
									) : (
										<div></div>
									)}
								</>
							}
						/>

						<Route path="/top-tracks" element={<div>Top Tracks</div>} />
						<Route path="/playlists/:id" element={<div>Paylist track</div>} />
						<Route path="/playlists" element={<div>Playlists</div>} />
					</Routes>
				</>
			)}
		</div>
	);
}

export default App;
